INSERT INTO `m_blog`(`id`, `user_id`, `title`, `description`, `content`, `created`, `status`, `categeory`, `collect`, `province`, `color`, `img`) VALUES (1, 1, '2021逛公园', '行程：颐和园耕织图景区>月坛公园>中山公园>非凡足球场(奥森北园店)>明城墙遗址公园>北京植物园>玉渊潭公园>北京凤凰岭景区>陶然亭公园', '内容？？？', '2020-05-21 22:08:42.000000', 0, 0, 0, '北京', NULL, 'https://tr-osdcp.qunarzz.com/tr-osd-tr-mapi/img/f2bbaa6f597cd2e6e29db744c7b0e486.jpg_200x150x95_dafc632c.jpg');
INSERT INTO `m_blog`(`id`, `user_id`, `title`, `description`, `content`, `created`, `status`, `categeory`, `collect`, `province`, `color`, `img`) VALUES (9, 1, '上海圣诞三天小漫游', '行程：虹桥国际机场>老城隍庙小吃>南翔馒头店>豫园>上海新协通国际大酒店>021上海菜主题餐厅(南京东路店)>南京路步行街>外滩>小杨生煎(...', '### 什么是事务  \n\n> 事务是应用程序中一系列严密的操作，所有操作必须成功完成，否则在每个操作中所作的所有更改都会被撤消。也就是事务具有原子性，一个事务中的一系列的操作要么全部成功，要么一个都不做。\n> \n> 事务的结束有两种，当事务中的所以步骤全部成功执行时，事务提交。如果其中一个步骤失败，将发生回滚操作，撤消撤消之前到事务开始时的所以操作。\n\n**事务的 ACID**\n\n事务具有四个特征：原子性（ Atomicity ）、一致性（ Consistency ）、隔离性（ Isolation ）和持续性（ Durability ）。这四个特性简称为 ACID 特性。\n\n> 1 、原子性。事务是数据库的逻辑工作单位，事务中包含的各操作要么都做，要么都不做\n> \n> 2 、一致性。事 务执行的结果必须是使数据库从一个一致性状态变到另一个一致性状态。因此当数据库只包含成功事务提交的结果时，就说数据库处于一致性状态。如果数据库系统 运行中发生故障，有些事务尚未完成就被迫中断，这些未完成事务对数据库所做的修改有一部分已写入物理数据库，这时数据库就处于一种不正确的状态，或者说是 不一致的状态。', '2020-05-22 22:04:46.000000', 0, 3, 0, '上海', NULL, 'https://img1.qunarzz.com/travel/d9/1601/6c/3e08b3828a0fe1f7.jpg_200x150x95_6375af2e.jpg');
INSERT INTO `m_blog`(`id`, `user_id`, `title`, `description`, `content`, `created`, `status`, `categeory`, `collect`, `province`, `color`, `img`) VALUES (19, 2, '当三亚遇到西雅图', '观日岩>蜈支洲岛>大东海>天涯海角>南山寺>海棠湾>亚龙湾>大小洞天>三亚湾>情人桥>凤凰岛>亚龙湾中心广场>皇后湾>龙仔湾>西...', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\"><html xmlns=\"http://www.w3.org/1999/xhtml\"><head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\"/><meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" /><meta name=\"keywords\" content=\"当三亚遇到西雅图-三亚旅游攻略-游记-笔记-2014/09/19-7-12000-独自一人-美食-海滨海岛，自助游攻略_去哪儿攻略\" /><meta name=\"description\" content=\"当三亚遇到西雅图,三亚旅游攻略/自助游攻略.去哪儿攻略社区提供旅游攻略制作、分享、下载，攻略包含景点、美食、酒店、购物、交通等信息，亲身体验过的旅游线路，当地实用信息，海量游记照片，国内外旅游攻略尽在去哪儿攻略社区。\" /><title>当三亚遇到西雅图-三亚旅游攻略-游记-去哪儿攻略</title><link rel=\"stylesheet\" href=\"//q.qunarzz.com/travel_new_plan/prd/styles/notes/detail@792283aee68074a2ebf4b2faab56d004.css\" type=\"text/css\" >\r\n<script type=\"text/javascript\">	\r\n      var QZZ_TRAVEL_MODULES = {\r\n                 \"usercard\" : [\"//q.qunarzz.com/travel_new_plan/prd/scripts/common/usercard@8a9af20c0ba97942cd07cf485d0e6916.js\"],\r\n                 \"download\" : [\"//q.qunarzz.com/travel_new_plan/prd/styles/css/dialog_download@22928fbc84316b09abb7a201479a9264.css\",\r\n                              \"//q.qunarzz.com/travel_new_plan/prd/scripts/common/dialog_download@cbdeea634e130ed71a52218ab0bebc74.js\"],\r\n                 \"login_panel\" : [\"//q.qunarzz.com/travel_new_plan/prd/scripts/common/travel_login@9463fc9d8c61122a9d5c03083475e033.js\"],\r\n                 \"adcommon\" : [\"//q.qunarzz.com/travel_new_plan/prd/styles/css/ad_common@f448aab10262f5e9404d6a0d39730346.css\",\r\n                               \"//q.qunarzz.com/travel_new_plan/prd/scripts/common/ad_common@c10f052a909044e2de5a2f1b753182d0.js\"]\r\n         };\r\n</script>\r\n<script>versionHeadImage=false;noteCreateTime=1427853550284;<