= undefined;var __LOGIN_USER__ = undefined;var __AUTHOR_USER__ = {\"userId\":\"179845589@qunar\",\"gender\":\"0\",\"headImg\":\"https://qcommons.qunar.com/headshot/headshotsById/179845589.png?s&ssl=true\",\"largeHeadImg\":\"https://qcommons.qunar.com/headshot/headshotsById/179845589.png?l&ssl=true\",\"middleHeadImg\":\"https://qcommons.qunar.com/headshot/headshotsById/179845589.png?m&ssl=true\",\"nickName\":\"厚道一号\",\"spaceBadge\":null,\"userUgcStatistics\":null,\"qunarUserId\":179845589,\"bind\":false,\"tempUser\":false,\"loginTime\":0,\"verified\":false,\"points\":0,\"oauth\":false,\"birthday\":\"2015-01-01\",\"designation\":null,\"travelCreator\":false,\"orgCreator\":false,\"certificationTitle\":null,\"smartTraveler\":true,\"qunarUser\":true,\"oauthUser\":false,\"domain\":\"qunar\"};var __WEIBO_SHARED_CONTENT__ = \"发现一篇#去哪儿游记#【当三亚遇到西雅图】目的地:三亚;行程亮点:天涯海角-三亚湾-亚龙湾热带天堂森林公园等。超级干货有木有！ 速来围观！\";var __QZONE_SHARED_CONTENT__ = \"发现一篇#去哪儿游记#【当三亚遇到西雅图】目的地:三亚;行程亮点:天涯海角-三亚湾-亚龙湾热带天堂森林公园等。超级干货有木有！ 速来围观！\";var __BOOK_TITLE__ = \"当三亚遇到西雅图\";</script><script src=\"//q.qunarzz.com/jquery/prd/jquery-1.7.2.min.js\"></script><script src=\"//q.qunarzz.com/travel_new_plan/prd/scripts/common/video@9de4f78e8c702d89c77512ce3dff9bb6.js\"></script><script src=\"//q.qunarzz.com/travel_new_plan/prd/scripts/notes/detail@7a13933df142a3d1ff1104e0f24d3e4b.js\"></script><script>var _hmt = _hmt || [];(function() {var hm = document.createElement(\"script\");hm.src = \"//hm.baidu.com/hm.js?c56a2b5278263aa647778d304009eafc\";var s = document.getElementsByTagName(\"script\")[0];s.parentNode.insertBefore(hm, s);})();</script></body></html>', '2022-04-03 16:53:08.000000', 0, 2, 0, '三亚', '#5e90b8', 'https://img1.qunarzz.com/travel/d9/1504/15/6a2a30df4e7de4.jpg_r_1050x591x95_60a2813e.jpg');
INSERT INTO `m_blog`(`id`, `user_id`, `title`, `description`, `content`, `created`, `status`, `categeory`, `collect`, `province`, `color`, `img`) VALUES (20, 2, '冬季游北京，不可错过的旅行', '行程：护国寺小吃>天安门广场>南锣鼓巷>什刹海>圆明园>清华大学>水立方（国家游泳中心）>鸟巢>北京饭店>人民大会堂>天安门城楼>后海...\n', '\n\n# <span class=\"title\" id=\"booktitle\">冬季游北京，不可错过的旅行</span>\n</div></div></div><div class=\"b_foreword\">\n\n*   出发日期<span class=\"line\">/</span><span class=\"data\">2015/01/01</span>\n\n*   天数<span class=\"line\">/</span><span class=\"data\">4</span>天\n\n*   玩法<span class=\"line\">/</span><span class=\"data\"><span class=\"data\">冬季</span>&nbsp;<span class=\"data\">美食</span>&nbsp;</span></div><div class=\"b_panel_schedule\" id=\"b_panel_schedule\"><div class=\"e_main\" id=\"tpl_1\"><!-- e_day start --><div id=\"day-15735476\" data-dayidx=\"day-15735476\" class=\"e_day day_style0 day-15735476\">\n\n#### <dl class=\"day_title\"><dt class=\"clrfix\"><div class=\"style\"></div><div class=\"text\">前言</div></dt></dl>\n<!-- period_ct start --><div class=\"period_ct\"><div class=\"b_poi_info b_poi_item\" id=\"ele-1550163-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">说说这次旅行</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n这一次的北京行，是由许多的运气和巧合促成，虽然来也匆匆去也匆匆，可是也算是尽情领略到了北京-帝都的美丽，也圆了自己的首都梦，真心很赞。\n\n北京是一个很不错的城市，尽管来之前，身边有人诋毁它，有人赞赏它，但只有通过自己亲身经历，我才明白，我喜欢这座城市。它有历史，有积淀，有活力，有机会，还有浓浓的北京风味儿，这一切都令我着迷。虽然我不喜欢这里快节奏的生活，但是我不介意来这里进行短暂的停留，总之一句话，北京是一个很有魅力的城市，它跟我之前去过的很多地方都不太一样，它处处都让人感觉到一种独特的美，令人难以忘怀。\n\n许多人爱旅行，其实是爱这样一种状态：啊，开始旅行啦！好难得出门啊！我要过另外一种生活！我要把工作都抛掉！我要把平时攒的钱都用上！我要把日常对自己的压榨和抠门，都在旅行中找补回来。\n\n在旅行中，人们就像是面对情妇一般，因为过上了远离日常生活的另一种未知而新鲜的生活，于是格外的慷慨，特别好奇，尤其大惊小怪，经常一惊一乍，你会更认真地区端详他乡的天空，而你在故乡可能从来没有注意过天空的颜色；他乡的狗都比较可爱，尽管你在你的故乡可能特别讨厌狗；他乡的空气都那么沁人心脾，而家乡的空气，你从来没有仔细闻过；他乡的劣质手工小商品，颜色似乎都比家乡的鲜艳些，能让你情不自禁的掏出钱包来。\n\n这也让我开始反思，热爱旅行的人们如我，还没有仔细的去感受过自己的家乡。而我的家乡重庆，对许多人来说又何尝不是一个旅行的目的地呢。人们总是忽略身边拥有的人和事，而把关注的眼光投到远方。我不想再这样了。突然就厌倦了旅行，决定再接下来的一段时间，我要用心的去感受家乡重庆，以旅行的心态去感受她，以旅行的眼光去看待它，以旅行的方式去发现她，我也希望借此能够学会珍惜拥有。\n\n与其说旅行比生活美满得多，不让说我们试图让旅行比生活美满。旅行和梦不一样，做梦你不\n\n知道何时结束，而旅行你知道何时结束。许多人都存在着这样一种心思：放在身边的，随时都能够观赏，所以还是先瞻望远处好了，身边的东西下次再说，下次再说。。。很多时候，就这么下次再说，终于是错过了。如果不能明白这里道理，或许有一天当我真的踏遍了全中国，走遍了全世界，回过头来却发现自己最不了解的地方是自己的家乡，我简直无法想象那场景会是多么的讽刺。\n\n不总指望着把好好享受的希望寄托在别处、未来和旅途上，而是尝试把自己的生活安排得好些，不总指望着命运给你埋伏着许多段一见钟情，而对身边的伴侣好一些，那样，生活会更加的真实而幸福。平时对自己好一些，旅行时才能少些幻想，真正的去体验旅行的快乐。\n\n谁不想去看看帝都，谁不想去爬爬长城，谁不想去尝尝烤鸭。谁不想在北京的胡同中流连，谁不想在北京的繁华中踟蹰，谁不想在北京的包容里奋斗。帝都有太多值得流连的东西，在每个人心目中应该都是心向往之的地方，就算不想在那里生活，也想在那里留下足迹，就像我。当一个多月以前知道自己有机会到北京去的时候，心情那叫一个激动。\n\n对北京的第一印象，还是来源于小时候看过的电视剧。这一部电视剧叫做《搭错车》，对这部电视剧印象很深刻，因为这部电视剧是与家人一起看完的。小的时候，情绪就已经很丰满了，看到煽情的地方眼睛会湿润。《搭错车》里面讲的是一个比较普通但却催人泪下的故事，是一个单亲家庭中父亲和女儿之间的点点滴滴。大陆看到的《搭错车》是改编自台湾同名电影《搭错车》\n\n（在这里特别想提到《搭错车》的主题曲&mdash;&mdash;《酒干倘卖无》，歌名是闽南语，意思是：有没有空酒瓶子可以卖？电影版搭错车没有进入大陆，但是《酒干倘卖无》却是风靡内地，是台湾流行音乐进军内地的先驱）。\n\n故事背景从宝岛台湾移至80年代初的北京，我对北京的第一印象就来自于这里。印象中的北京，人们总是骑着自行车或者三轮车，穿行在各路胡同中，因为天冷，所以大街上的人们总是喜欢在大街上双手一边搓一边哈气，哈出来的气息形成了一串明显的烟雾状。电视剧中令人印象最深刻的，就是随处可以听到，特色鲜明的京腔了，儿化音总是令我很惊奇，也莫名向往。从那时候起，就有了想去北京的想法，只是没有到这一天来得这样快。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论_(1)_](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550213-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">小贴士</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n【北京】北京，每一个国人都不陌生的地方，天朝的首都，一般我们会称之为帝都，有点儿资本主义的意思，哈哈。因为它特殊的地位，这是一个令每个国人都向往的地方，毕竟，自古以来，天子脚下就是一个充满机遇，繁华热闹的地方。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550214-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">交通</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n帝都的交通，除了吐槽一下太挤，就真没有其他不舒服的地方了。往来帝都，从任何地方都是很方便的，这绝对是全国最大的交通枢纽，飞机，火车，汽车，绝对是物美价廉。北京市内的交通，那就更加方便了，几十年历史的地铁，近百年历史的公交车，无数的的士，会让你的出行更加方便。还有一点儿要吐槽的就是，早就听闻帝都的交通便宜，地铁2元可以坐到底，可是我们去之前已经涨价了，没有享受到帝都便宜交通总是令我们耿耿于怀，哈哈。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550215-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">美食</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n老北京的美食，这次并没有可以的去品尝。北京是一个多民族人口汇聚地，这里可以吃到全国各地美食，所以反倒没有特意的去品尝本地的美食。真正品尝过的美食有涮羊肉，老胡同里的油饼，小肠，豆花，都还是不错的。护国寺小吃在同学的推荐下去吃了，说实话感觉一般，不过护国寺小吃价格并不贵，这个可以有。至于豆汁儿，简直没有勇气尝试。只有下次去要去常常道地的本地美食。如果有推荐的，可以给我留言哦。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550216-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">住宿</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n北京的住宿还是蛮方便的，要说建议的话，只能跟我自身情况来说了。现在全国各地涌现出了越来越多的青年旅社，除了国际青年旅社比较靠谱有意思以外，许许多多的私人青旅或者正规青年旅社也非常的赞，价格实惠，环境好，老板一般人也好，住青旅的性价比很高，特别独自旅行的时候，可以在青旅找到旅伴，说不定还能够有一段浪漫邂逅啥的。这一次帝都之行，还是一如既往订的青旅，网上找了一家评价还不错的青旅，60元一晚六人间，结果去了之后免费升级为四人间还一个人住，这人品啊，今年订的青旅有一半都被免费升级了房间。当然，如果资金充裕，那就非常好选择了，各种价位的酒店基本随便选。如果想要方便住宿，那么淡季才出发吧。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![住宿图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"住宿图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741513\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论_(1)_](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550219-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">路线</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n北京嘛，好玩儿的地方就太多了，要玩儿尽兴的话，起码8到10天。没啥好计划的，北京这么多好地方，时间充裕那就都去看看，时间不充裕就捡感兴趣的看看，基本看着旅行地图计划还是很方便的，帝都的交通很方便，这个必须点赞，提醒一点儿一定要注意错开出行高峰期，不然你就会有机会尝试一下帝都的拥挤，那滋味太难忘。反正玩儿得开心，玩儿得尽兴最重要。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div></div><!-- /period_ct end --></div><!-- /e_day end --><!-- e_day start --><div id=\"day-15735480\" data-dayidx=\"day-15735480\" class=\"e_day day_style0 day-15735480\">\n\n#### <dl class=\"day_title\"><dt class=\"clrfix\"><div class=\"style\"></div><div class=\"text\">第1天</div></dt></dl>\n<!-- period_ct start --><div class=\"period_ct\"><div class=\"b_poi_info b_poi_item\" id=\"ele-15148535-4\" data-poitype=\"4\"  poi-id=\"711995\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[天安门广场](https://travel.qunar.com/p-oi711995-tiananmenguangchang \"天安门广场\")</div>[59篇游记中提到](//travel.qunar.com/travelbook/list/4-tiananmenguangchang-711995.htm)<div data-eleId=\"711995\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费开放\n*   <span>电话：</span>010-86409660,010-63095745\n*   <span>地址：</span>北京市东城区长安街（天安门和正阳门之间）\n*   <span>简介：</span>伟大祖国的标志，早起看一场庄严肃穆的升旗仪式，瞻仰人民英雄纪念碑。\n*   [查看详情](https://travel.qunar.com/p-oi711995-tiananmenguangchang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n这次旅行一共四天，出发时状态很狼狈。飞机一再晚点，还飞到天津去降落了一次，原定凌晨一点钟到的飞机，一直到早上七点钟才降落在首都机场，所以第一天没有看成升旗仪式。不过在北京的四天一直是艳阳天，这点确实感觉很幸运，在北京见了一些有段时间没见的朋友，也是留下了许多共同美好的记忆，这也是旅行的价值之一，毕竟人老了以后就只剩下回忆了，所以要趁年纪轻的时候多创造一些美好的东西。我算是看明白了，人的成长是一次一次的，而且都是需要经历了才能够成长，所以每次感觉长大了一点儿，我都很感恩。正常情况下，年龄确实是跟成熟度成正比的吧。成长的道路简直没有尽头。旅行使我成长。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![天安门广场图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"天安门广场图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741545\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在北京公共厕所方个便，隔壁传来的都是很纯正的京腔，哪种感觉绝逼是很独特的。在北京跟一个三年多不见的高中同学见了一面，熟悉的乡音不复存在，很默契的说着普通话，只不过我的是川普，而她早已经是一口纯正的京腔了，这一幕总是让我觉得很不适应，总是感觉哪里不对劲。这次在北京四天，每一天都是艳阳天，真心很感谢老天爷。不过没有见识到北京的雾霾，总还是有点儿遗憾（绝对没有装逼的意思）的。听我同学说在这里读书还要准备防毒面具，我当时就震惊了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![天安门广场图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"天安门广场图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741557\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在北京旅行有一个好处，就是那些在电视中看惯了的场景，在这里可以自己亲自去感受一下：鸟巢水立方，天安门，长城，全聚德，在现场看和在电视中看感受是很不一样的。来北京之前有同学跟我说不稀罕去北京的，后来我发了几张北京胡同的照片，她又改变主意说北京也不错。这一点儿让我就很感慨，我们竟然可以毫无理由的爱上或者厌上一座城市。我想说的是，不管你心里面是怎样看待北京这个城市的，只有亲自去感受了，你才会知道自己对这座城市真正的看法，也许，你会喜欢上这里对吧！\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148639-4\" data-poitype=\"4\"  poi-id=\"722949\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[南锣鼓巷](https://travel.qunar.com/p-oi722949-nanluoguxiang \"南锣鼓巷\")</div>[95篇游记中提到](//travel.qunar.com/travelbook/list/4-nanluoguxiang-722949.htm)<div data-eleId=\"722949\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天\n*   <span>地址：</span>北京市东城区南锣鼓巷胡同\n*   <span>简介：</span>老北京胡同与小资情调结合，文艺青年的必逛之地。\n*   [查看详情](https://travel.qunar.com/p-oi722949-nanluoguxiang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n经过一个晚上的折腾，早上七点飞机安全降落在了北京首都机场，在机场上了机场快线，准备前往市区和同伴相聚。在机场巴士上，心情就已经开始激动了，汽车驶过的地方，有些荒凉，有些萧条，这也是冬季北京的特色，路上全是一些光秃秃的树，好像听说是杨树，也不确定。虽然不想承认，不过在机场巴士上看见的这些场景，确实是我对北京的第一印象了。\n\n跟同伴约在了南锣鼓巷，来南锣鼓巷主要是感受北京的胡同。一到南锣鼓巷，很惊喜，想象中的画面一下就看见啦。只不过她的意义可能不太一样，现在更多的是景区交通吧。南锣鼓巷由许多的胡同组成，是一个蛮大的地方，白天的时候人并不是很多，夜晚的时候才热闹一些，夜晚的南锣鼓巷也更美。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741622\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741636\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n第一天的阳光超级好，看看这是不是骑着三轮车的人们，当时真是很开心。趁着等同伴起床的时光，先逛了逛南锣鼓巷，早晨的南锣鼓巷还是比较冷清的，不过这正合我意。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741654\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741653\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在南锣鼓巷也能够看见这些光秃秃的树，这是北京的一大特色，在每个地方都能够看得到，这是我对北京的一大印象。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741675\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148644-4\" data-poitype=\"4\"  poi-id=\"705983\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[什刹海](https://travel.qunar.com/p-oi705983-shenchahai \"什刹海\")</div>[55篇游记中提到](//travel.qunar.com/travelbook/list/4-shenchahai-705983.htm)<div data-eleId=\"705983\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天\n*   <span>电话：</span>010-83222231,010-66125717\n*   <span>地址：</span>北京市西城区东起地安门外大街北侧、南至新街口北大街、西至新街口豁口、北至钟、鼓楼的区域\n*   <span>简介：</span>历史街区簇拥的优雅湖泊，环境优美，也是北京夜生活的核心地带。\n*   [查看详情](https://travel.qunar.com/p-oi705983-shenchahai)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n北京的第一天，它给了我笑脸，阳光明媚，真是开心。在南锣鼓巷跟同伴见面了，有同伴，还有一个高中同学。跟高中同学的见面倒是挺期待的，见面之后，聊了很久，确实也有许多地方跟想象的不一样，还是很激动。大家边走边聊，走到了什刹海，这个地方可是早闻大名了。第一次见到结冰的湖面，就是什刹海，南方孩子真心觉得挺惊奇的。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![什刹海图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"什刹海图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741691\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![什刹海图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"什刹海图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741694\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n湖面上还有人在玩儿滑冰的游戏，我倒是很想下去试试，不过时间紧急，我们还要去参观百度公司，想想也是很激动。匆匆结束了跟老同学的见面，就赶往百度，我们去的地方是百度公司的百度地图和百度旅游的办公大楼，只有在门口拍了一张，进去之后没好意思拍。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148648-4\" data-poitype=\"4\"  poi-id=\"704630\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[圆明园](https://travel.qunar.com/p-oi704630-yuanmingyuan \"圆明园\")</div>[50篇游记中提到](//travel.qunar.com/travelbook/list/4-yuanmingyuan-704630.htm)<div data-eleId=\"704630\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>大门门票10元、西洋楼遗址景区15元\n*   <span>电话：</span>010 6262 8501\n*   <span>地址：</span>北京市海淀区清华西路28号\n*   <span>简介：</span>古老中国最深的一道伤疤，通过断壁残垣寻找当年帝国的辉煌掠影。\n*   [查看详情](https://travel.qunar.com/p-oi704630-yuanmingyuan)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n在百度参观了半个小时，在百度附近解决了中午饭之后就赶往下一个目的地&mdash;&mdash;圆明园遗址公园。\n\n想来这里，跟历史课也是很有渊源的，相信的每个童鞋都能够记得哪张圆明园遗址残垣断壁的照片，那是中华民族永远避不开的伤口。很快就到了圆明园遗址公园的一个门，不知道是哪个们。大门口的石狮很喜欢。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741719\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741729\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741731\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741730\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741737\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741738\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n走着走着就走到了那个非常熟悉的地方了，看了图片都明白。在这里拍了不少照片，这里就贴两张了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741742\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741741\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741744\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741743\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在这里遇见了一对旅行的中老年夫妇，很开心的玩儿着自拍，真令人羡慕。希望自己以后老了也能有这一份心吧，带着心爱的她。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741746\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741747\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741748\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741754\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741753\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741758\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741769\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n走着走着看见了一个很漂亮的桥，拍了好多照片。在这里也偷懒了，明明桥那边还有好多地方可以走的，可是两人都走累了，于是拍了拍照片就回去了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741775\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这里就可以看到明显的分界线，结了冰的湖面异常的平静，一点儿没有波澜；而没有结冰的那一半，还有淡淡的水纹。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741778\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在圆明园步行时间过长，而且找不到出去的路，走得各种累。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741785\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这个地方，我们走了两个来回，没错，就是两个来回。走累了，然后拍了下面这一张照片，很喜欢这种感觉，依稀记得跟某用户的头像非常像。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741786\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n天气好的时候，圆明园的风景很美，如果时间允许，真想找个地方坐坐，或者是就慢慢的走在这样的道路上，一种难得的享受啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741788\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741789\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![圆明园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"圆明园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741791\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在圆明园里面走得累死了，最后好歹是找到了出来的门，传说这个门到清华大学比较近，我们专门找到这个门出来的。出来就遇见了很好的光影，这一张照片是我很满意的一张，很喜欢。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148675-4\" data-poitype=\"4\"  poi-id=\"715110\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[清华大学](https://travel.qunar.com/p-oi715110-qinghuadaxue \"清华大学\")</div>[29篇游记中提到](//travel.qunar.com/travelbook/list/4-qinghuadaxue-715110.htm)<div data-eleId=\"715110\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>双休日、法定节假日及学校寒暑假的上午8:30-11:30，下午13:30-16:30\n*   <span>电话：</span>010-62793001,010-62785001\n*   <span>地址：</span>北京市海淀区双清路30号\n*   <span>简介：</span>赏水木清华之美景，品百年名校之韵味。\n*   [查看详情](https://travel.qunar.com/p-oi715110-qinghuadaxue)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n然后接下来就赶往了清华大学。\n\n作为中国最最有名的大学，这无疑是一个令人神往的地方。小的时候我就在想，以后长大了是上清华好还是上北大好，长大后才发现，我想多了。在清华大学门口遇见了一个很好心的大妈，主动的要带我们进入清华园，我们一开始还以为是骗子，后来才发现确实人家是好心，惭愧啊，很感谢大妈。\n\n在清华园里面逛逛感觉蛮不错，找了好久好歹是找到了那个著名的校门，赶紧拍几张。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![清华大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"清华大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741799\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![清华大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"清华大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741798\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n清华大学里面的风景蛮不错的，好羡慕这里的学生啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![清华大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"清华大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741803\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n个人觉得图里的风景是去过的清华大学里面最美丽的地方了，没有同学也不知道这里是干嘛的。很喜欢这种建筑风格，当然最关键的是这一天的阳光，天公作美啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![清华大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"清华大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741808\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在清华逛了逛，去校园里的邮局里购买了一些纪念品，大多是清华大学的，然后在这里给自己寄了一张明信片，就找路出去了。接着本来是想去北京大学的，结果在外面逛了逛，找不到路，好不容易找到一个门，又不让进。后来看时间比较晚了，就决定不去了。接着准备去奥体中心。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148738-4\" data-poitype=\"4\"  poi-id=\"706725\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[水立方（国家游泳中心）](https://travel.qunar.com/p-oi706725-shuilifang \"水立方（国家游泳中心）\")</div>[44篇游记中提到](//travel.qunar.com/travelbook/list/4-shuilifang-706725.htm)<div data-eleId=\"706725\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>参观票：30元；游泳票：50元；嬉水乐园票：200元\n*   <span>开放时间：</span>目前暂停对外开放，仅保留西侧7、8号门正常对外开放。详情请见官网\n*   <span>电话：</span>010-84370112,010-84370116\n*   <span>地址：</span>北京市朝阳区北辰路奥林匹克公园内\n*   <span>简介：</span>建筑外观独特，夜晚灯光美轮美奂，适合拍摄夜景。\n*   [查看详情](https://travel.qunar.com/p-oi706725-shuilifang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n老早就想去看看鸟巢和水立方，结果我们赶到奥体的时候已经是晚上了，这里妖风阵阵，冷的要死，就连相机都不想逃出来，只能用手机糊弄一下了。到奥体的时候绝对是来到北京所感受到最冷的时刻。不过在这里亲眼看到了鸟巢水立方，感觉很值得，只是没吃晚饭肚子饿着比较难受，囧。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![水立方（国家游泳中心）图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"水立方（国家游泳中心）图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741842\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148746-4\" data-poitype=\"4\"  poi-id=\"716377\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[鸟巢（国家体育场）](https://travel.qunar.com/p-oi716377-niaochao \"鸟巢（国家体育场）\")</div>[60篇游记中提到](//travel.qunar.com/travelbook/list/4-niaochao-716377.htm)<div data-eleId=\"716377\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>成人参观门票：40元\n*   <span>开放时间：</span>旺季：9:00-20:30；淡季：9:00-17:30\n*   <span>电话：</span>4000002008,010-84373008\n*   <span>地址：</span>北京市朝阳区国家体育场南路1号\n*   <span>简介：</span>08年北京奥运会的标志建筑，众多体育赛事和演唱会的举办地，是北京的新地标。\n*   [查看详情](https://travel.qunar.com/p-oi716377-niaochao)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><dl class=\"js_memo_node\"><dt style=\"width:680px\">![鸟巢（国家体育场）图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"鸟巢（国家体育场）图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741853\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550247-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">夜晚的北京</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n这里真心是相当的&mdash;&mdash;冷，呆了一会儿就准备走了，找了个地方吃老北京特色小吃&mdash;&mdash;涮羊肉。也是第一次吃涮羊肉，感觉还蛮不错的，两个人吃了有两三百吧。\n\n晚上的北京真心是蛮冷的，准备去的一定要注意保暖了。在北京的第一天就这样过了，走了很多路，脚很疼，回到青旅做的第一件事情就是赶紧洗个澡，很快就休息了，睡得叫一个香。\n</div></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div></div><!-- /period_ct end --></div><!-- /e_day end --><!-- e_day start --><div id=\"day-15735756\" data-dayidx=\"day-15735756\" class=\"e_day day_style0 day-15735756\">\n\n#### <dl class=\"day_title\"><dt class=\"clrfix\"><div class=\"style\"></div><div class=\"text\">第2天</div></dt></dl>\n<!-- period_ct start --><div class=\"period_ct\"><div class=\"b_poi_info b_poi_item\" id=\"ele-15148756-6\" data-poitype=\"6\"  poi-id=\"4276899\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[北京饭店](https://travel.qunar.com/p-oi4276899-beijingfandian \"北京饭店\")</div>[1篇游记中提到](//travel.qunar.com/travelbook/list/2-beijingfandian-4276899.htm)<div data-eleid=\"15148756\" class=\"piao_info clrfix\" style=\"display: inline-block;\"><div class=\"price\"><span class=\"pr\">_¥_2650</span>起</div>[预订](https://travel.qunar.com/p-oi4276899-beijingfandian)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>电话：</span>010-65137766\n*   <span>地址：</span>北京东城区东长安街33号\n*   <span>简介：</span>北京饭店位于市中心，毗邻王府井商业区，步行数分钟即可到达天安门广场和故宫博物院，地理位置得天独厚。饭店东侧与驰名中外的王府井商业街、东方广场商业区举步之遥；西侧与雄伟的天安门、恢弘的皇宫紫禁城相邻，尽\n*   [查看详情](https://travel.qunar.com/p-oi4276899-beijingfandian)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n今天很任性，睡到十点多钟才起床的，昨天真心累得不行。本来今天早上和人约好去看天安门升国旗和故宫的，结果没去成，如果我知道错过这次机会这次北京行就没机会去故宫了，我一定能起床的，只是世上没有后悔药。算是一个小小遗憾。\n\n我住在北京东城区细管胡同，起床了索性就先逛了逛这个胡同，感觉确实蛮不错的，很有特色，跟我想象中的老北京胡同一样。找了个地方吃早餐，瞄准了老北京特色，价格竟然不贵我表示很惊奇。\n\n要去天安门附近接一个同伴，于是决定先去逛逛天安门。坐公交车到长安街前一个站，想着走到天安门去，没想到这一站竟然是这么远，走残了。还是很值得，长安街给人感觉很霸气，路面很霸气，两边的建筑也是那么霸气，很喜欢。在长安街看到的第一个建筑就是著名的北京饭店，老早就知道了。\n\n这是一个很有名的地方，听过一些关于他的故事。北京饭店位于长安街上，离天安门都不远了。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京饭店图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京饭店图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741879\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京饭店图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京饭店图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741882\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148790-4\" data-poitype=\"4\"  poi-id=\"713754\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[人民大会堂](https://travel.qunar.com/p-oi713754-renmindahuitang \"人民大会堂\")</div>[9篇游记中提到](//travel.qunar.com/travelbook/list/4-renmindahuitang-713754.htm)<div data-eleId=\"713754\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>30元\n*   <span>开放时间：</span>四季开放时间不同，7-8月：7:30-16:00\n*   <span>电话：</span>010-63096156\n*   <span>地址：</span>北京市西城区西长安街天安门广场西侧\n*   <span>简介：</span>全国人民代表大会会址，建筑恢宏壮观，内部灯火辉煌。\n*   [查看详情](https://travel.qunar.com/p-oi713754-renmindahuitang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n在长安街上走着，两边的建筑都是那种很庄严的感觉。没有去做攻略，也不知道这些建筑都是干啥的。以至于回来之后老爸问我人民大会堂是不是在天安门对面我都答不上来，囧。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![人民大会堂图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"人民大会堂图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741898\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550251-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">出发去天安门</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n不一会儿，就看到了红墙黄瓦的经典建筑了，心情激动ing。第一次嘛，总是印象深刻的。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去天安门图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去天安门图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741916\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去天安门图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去天安门图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741922\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n准备去天安门，想去看天安门的话需要排队，虽然现在正是旅行淡季，但是看着前面的队伍，也是醉了。这要是旺季的话得排多长的队啊。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148870-4\" data-poitype=\"4\"  poi-id=\"711995\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[天安门广场](https://travel.qunar.com/p-oi711995-tiananmenguangchang \"天安门广场\")</div>[59篇游记中提到](//travel.qunar.com/travelbook/list/4-tiananmenguangchang-711995.htm)<div data-eleId=\"711995\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费开放\n*   <span>电话：</span>010-86409660,010-63095745\n*   <span>地址：</span>北京市东城区长安街（天安门和正阳门之间）\n*   <span>简介：</span>伟大祖国的标志，早起看一场庄严肃穆的升旗仪式，瞻仰人民英雄纪念碑。\n*   [查看详情](https://travel.qunar.com/p-oi711995-tiananmenguangchang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n天安门，绝对是每个国人爱国信仰的所在，其意义堪比自由女神像之于美国，卢浮宫之于法国，凯旋门之于英国。所以在远远瞄见天安门的时候，我的心情是真的激动了。只是我发现，天安门咋没有想象中的那么大啊。不过这一点儿也不妨碍心情。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![天安门广场图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"天安门广场图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741933\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![天安门广场图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"天安门广场图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741953\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n对于天安门，总是有一种看不够的情绪，不知道其他人是不是也这样啊，总之，要走了还拍了不少照片。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550258-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">百度旅游首届行程规划咨询师峰会</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n峰会过程就不详述了，官网上有资料，贴几张照片吧，很有意思的峰会。\n\n在峰会上最大的收获之一，就是知道了凯德大卫和凯德印象的故事。这是一对男同性恋，已经相恋十年，并且已经于新加坡登记结婚。他两的故事，也是关于爱情，也是关于旅行，能够找到这样一个人陪自己，浪迹天涯，羡慕死他们，我的老婆你现在在哪儿啊？？会议结束后赶紧找他两合了个影，希望他两能够一直好下去，一起去完成环游世界的大业。至少目前看来，他两进行的挺不错的，祝福给他们。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![百度旅游首届行程规划咨询师峰会图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"百度旅游首届行程规划咨询师峰会图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741968\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![百度旅游首届行程规划咨询师峰会图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"百度旅游首届行程规划咨询师峰会图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741976\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n照片是我们西南区咨询师代表与百度旅游掌门合影，掌门好帅好幽默，向他学习！西南区的咨询师都棒棒的，他们分别是环球小姐，庞尼西，还有一个未能到来的豆豆。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![百度旅游首届行程规划咨询师峰会图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"百度旅游首届行程规划咨询师峰会图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741981\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n然后就是百度旅行小精灵合影了，都是青春美少女，看看能否对上号吧。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![百度旅游首届行程规划咨询师峰会图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"百度旅游首届行程规划咨询师峰会图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741984\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n咨询师峰会结束已经快七点钟了，我们一些小伙伴约好一起出去玩儿，先回酒店略作修正，就出发了，一群人，很开心。出门就看到了一辆特斯拉，赞一个。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![百度旅游首届行程规划咨询师峰会图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"百度旅游首届行程规划咨询师峰会图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741987\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148896-4\" data-poitype=\"4\"  poi-id=\"3721859\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[后海公园](https://travel.qunar.com/p-oi3721859-houhaigongyuan \"后海公园\")</div>[39篇游记中提到](//travel.qunar.com/travelbook/list/4-houhaigongyuan-3721859.htm)<div data-eleId=\"3721859\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天\n*   <span>地址：</span>北京市西城区松树街1号\n*   <span>简介：</span>什刹海的组成部分，白天胡同京味十足，夜晚酒吧霓虹闪烁。\n*   [查看详情](https://travel.qunar.com/p-oi3721859-houhaigongyuan)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n直接乘坐地铁去后海，不过没打算去泡酒吧，只是想去感受一下，有机会一定是要再来后海泡泡这里的酒吧的。\n\n后海是北京有名的地方，这个地方最多的就是各种酒吧了。夜晚的时候这里总是人流涌动，有游客，有本地人，有外地人，来这里泡酒吧是一些人的生活方式了吧，我也挺想去尝试的，一次就好，只是这次没机会，留待下次了吧。后海的夜景很漂亮，我们在这里各种合影。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![后海公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"后海公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7741996\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![后海公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"后海公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742005\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![后海公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"后海公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742006\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n接下来就各种在后海玩儿自拍，小伙伴们好嗨皮。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![后海公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"后海公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742015\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n后海是一个不错的地方，估计消费也比较贵吧，反正这次我们是没有泡酒吧。一来到后海，就会有人来来问你要不要泡吧啥的，有机会去试试应该挺不错的。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148903-4\" data-poitype=\"4\"  poi-id=\"722941\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[烟袋斜街](https://travel.qunar.com/p-oi722941-yandaixiejie \"烟袋斜街\")</div>[25篇游记中提到](//travel.qunar.com/travelbook/list/4-yandaixiejie-722941.htm)<div data-eleId=\"722941\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天\n*   <span>电话：</span>010-64001606\n*   <span>地址：</span>北京市西城区地安门外大街48号\n*   <span>简介：</span>京味儿十足的一条繁华老街，逛逛钟表铺、毛笔铺、老字号小吃店。\n*   [查看详情](https://travel.qunar.com/p-oi722941-yandaixiejie)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n我们接下来决定去烟袋斜街，烟袋斜街离后海也不算远。\n\n这是北京一条非常古老的商业街，街道并不宽，感觉古色古香。印象比较深刻的是这里一个表演的艺人，就在路边，忘情的拉着二胡，浑然忘我。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![烟袋斜街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"烟袋斜街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742052\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![烟袋斜街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"烟袋斜街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742049\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148931-4\" data-poitype=\"4\"  poi-id=\"722949\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[南锣鼓巷](https://travel.qunar.com/p-oi722949-nanluoguxiang \"南锣鼓巷\")</div>[95篇游记中提到](//travel.qunar.com/travelbook/list/4-nanluoguxiang-722949.htm)<div data-eleId=\"722949\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天\n*   <span>地址：</span>北京市东城区南锣鼓巷胡同\n*   <span>简介：</span>老北京胡同与小资情调结合，文艺青年的必逛之地。\n*   [查看详情](https://travel.qunar.com/p-oi722949-nanluoguxiang)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n大家都是各种意犹未尽，于是再相约前往南锣鼓巷，虽然我已经来过两次这里了，这是第三次啦。\n\n听同学说这里的酸奶很是值得一试，于是果断鼓捣同伴去试试，环球小姐很大方的表示要请客，赞一个。这里的酸奶味道还是蛮不错的，夜晚的南锣鼓巷也比白天更有魅力了。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742059\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![南锣鼓巷图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"南锣鼓巷图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742062\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15148935-6\" data-poitype=\"6\"  poi-id=\"4262616\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[北京国家会议中心大酒店](https://travel.qunar.com/p-oi4262616-beijingguojiahuiyizhong \"北京国家会议中心大酒店\")</div>[1篇游记中提到](//travel.qunar.com/travelbook/list/2-beijingguojiahuiyizhong-4262616.htm)<div data-eleid=\"15148935\" class=\"piao_info clrfix\" style=\"display: inline-block;\"><div class=\"price\"><span class=\"pr\">_¥_986</span>起</div>[预订](https://travel.qunar.com/p-oi4262616-beijingguojiahuiyizhong)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>电话：</span>010-84372008\n*   <span>地址：</span>北京朝阳区北辰西路8号院1号楼\n*   <span>简介：</span>北京国家会议中心大酒店与国家会议中心连廊相通，步行可达鸟巢、水立方、国家体育馆，邻近地铁8号线奥林匹克公园，出行非常方便。 北京国家会议中心大酒店在北京奥运会期间为新闻中心，地位举足轻重。现今酒店客房均\n*   [查看详情](https://travel.qunar.com/p-oi4262616-beijingguojiahuiyizhong)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n再逛了逛然后大家终于都累了，一起赶回到我们住的酒店。今晚住在国家会议中心大酒店，高大上有木有。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京国家会议中心大酒店图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京国家会议中心大酒店图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742066\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京国家会议中心大酒店图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京国家会议中心大酒店图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742071\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在北京的第二天就这样过去了，晚上睡的很香，感谢百度旅游，希望百度旅游越来越好。下面就是我住的房间了，环境挺好的，还送了一盆盆栽，赞一个，盆栽被我送给北京的同学了，自己都没看到。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div></div><!-- /period_ct end --></div><!-- /e_day end --><!-- e_day start --><div id=\"day-15735834\" data-dayidx=\"day-15735834\" class=\"e_day day_style0 day-15735834\">\n\n#### <dl class=\"day_title\"><dt class=\"clrfix\"><div class=\"style\"></div><div class=\"text\">第3天</div></dt></dl>\n<!-- period_ct start --><div class=\"period_ct\"><div class=\"b_poi_info b_poi_item\" id=\"ele-15148943-4\" data-poitype=\"4\"  poi-id=\"713340\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[中华民族博物院](https://travel.qunar.com/p-oi713340-zhonghuaminzubowuyuan \"中华民族博物院\")</div>[1篇游记中提到](//travel.qunar.com/travelbook/list/4-zhonghuaminzubowuyuan-713340.htm)<div data-eleId=\"713340\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>90元（通票）\n*   <span>开放时间：</span>8:00-22:00\n*   <span>电话：</span>010-62063647,010-62063646\n*   <span>地址：</span>北京市朝阳区民族园路1号（健翔桥东路口）\n*   <span>简介：</span>参观各民族特色建筑，欣赏民俗表演，穿民族服装拍照留念。\n*   [查看详情](https://travel.qunar.com/p-oi713340-zhonghuaminzubowuyuan)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n今天跟两个大学同学约好在北京一起玩儿，他们分别是常老板和蒋老板（偷笑）。今天我们一共走了三万步，二十公里，真是令人难以置信啊。一大早我们在地铁站相约见面，许久不见大家变化并不大，还是熟悉的样子，真开心。\n\n听北京土著常老板的话，我们决定去一个不太挤又有意思的地方&mdash;&mdash;北京中华民族园。经过一番纠结，再绕了一大圈弯路之后，我们终于是成功的进入了中华民族园。\n\n今天在中华民族园的经历绝对算是奇特了，感觉整个公园北咱三个包场了有木有，逛了一大圈，基本没有遇见啥人，这在北京绝对算是经历奇特了吧。围着民族园走了找了好大一圈儿，总算是找到了民族园的入口。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742085\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n本来我提议给给咱们三个来张合影，结果这两货都不愿意，都是男生好嘛。于是只好给大门来了张特写。中华民族园的门票不便宜，学生打半折，常老板没证，购了全票。\n\n进入中华民族园之后，我们拿到了一张节目单，粗略一看，发现有一场节目表演马上就要开始了，赶紧奔了过去，结果就开始了我们在民族园的第一个包场，包场了整场表演好嘛。我们赶到表演地点的时候，发现演员很多，观众一个都没有，于是观众就咱三个人，赶紧留照片为证。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742091\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n除了咱三个坐在前排中央，其他人都是这个场的演员们，头一次得到这样的待遇，心情还是小激动了一把。常老板和蒋老板总是这么害羞，拍个照还要遮脸，哎。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742097\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这里的表演有各个民族的，个人非常的喜欢。不过想着这么多人表演，观众就咱三个人加上后来稀稀拉拉的来的几个人，感觉还是蛮尴尬的，特别是我们还坐在最前排中央，只有使劲鼓掌来掩饰尴尬了。贴几张演出照。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742100\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742099\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742143\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这场表演大概持续了不到半个小时，结束的很快，有种意犹未尽的感觉。看完表演就准备出来逛民族园了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742196\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n中华民族园里面有中国应该是所有少数民族的博物馆，每个民族都有对应的地方进行民族特色展示，感觉还不错。今天人真心少，整个上午没看见几个人，有的博物馆也没有开门，不过我们老同学聚在一起互相打趣，闲逛来还是蛮有意思。\n\n民族园里面有许多的猫咪。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742201\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n常老板出场了，此人乃是北京土著，刚巡视完一民居出来。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742204\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n民族园里面有些地方我们并没有去细看，下面是一个环形圆台。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742221\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n遇见一只很奇特的猫咪，不明觉厉。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742228\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n重点来了，遇见一个直立秋千，大家都童心未泯，挨个上去尝试了一把，瞬间年轻了十岁有木有。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742233\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n同行的蒋老板了，中科院的高材生，令人羡慕啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742249\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n民族园里面的景色细看之下还是蛮不错的，加上这一天天气也还不错，所以感觉很好。一路行来大家都拍摄了一些照片，当然除了常老板。这里的风景对于我来说是非常=奇特的，结了冰的湖面，到处都是垂柳和一些特别的光秃秃的树。我们就湖面能否站人讨论了很久，到底也没有去湖面上走一走，算是一个小遗憾。现在想着，就觉着应该勇敢的湖面上走一遭才对的。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742250\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742252\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742259\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742267\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742270\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n常老板回眸一笑。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![中华民族博物院图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"中华民族博物院图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742322\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在民族博物馆里面逛了有两三个小时，大家都有点儿疲惫了。不过里面的风景真心很不错的，直接上图了。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550272-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">满族博物馆</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n逛着逛着就到了满族博物馆，作为大清皇族，满族博物馆显得非常高端大气上档次，可惜也没有人在里面，只能看看院子啥的。红墙黄瓦的建筑，给人很庄严的感觉。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742290\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742289\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742298\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742300\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742306\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n很喜欢这个大院门前的一对石狮子\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742386\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742385\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742387\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742389\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742391\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742390\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742392\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n前段时间刚去了康定，新都桥，塔公，在那里第一次看见了转经筒，没想到转个身在北京民族园里面又看到了，很怀念的感觉。常老板和蒋老板都去转了一整圈儿。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![满族博物馆图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"满族博物馆图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742393\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在藏传佛教寺庙里面遇见一对情侣守门人，我们进去的时候刚好打扰了人家的好事儿，囧了个囧。门票一元，去里面看了个啥菩萨，也不认识，不过还是很虔诚的一人烧了一炷香。后来终于是逛了出来，准备找地方吃午饭。走在大街上，今天的风并不大，阳光下的北京简直令人迷醉，很喜欢帝都的这种feel，不要雾霾。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149067-5\" data-poitype=\"5\"  poi-id=\"0\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">西域食府</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>地址：</span>延庆县小营商业街师范路东19号(近九州宾馆、电影院)\n*   <span>电话：</span>010-61125788<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n中午饭是在西域食府解决的，北京土著常老板请客。这里的菜没啥北京特色，我们点了烤肉串，馕等，感觉还不错，三个人吃了两三百，感觉有点儿贵。常老板作为品酒师，还自带了一瓶红酒，三人中估计也就常老板会喝红酒了，我们俩也就当水喝，囧。\n</div><dl class=\"js_memo_node\"><dt style=\"width:383px\">![西域食府图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"西域食府图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742394\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![西域食府图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"西域食府图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742396\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:383px\">![西域食府图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"西域食府图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742395\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![西域食府图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"西域食府图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742397\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550283-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">出发去五道口</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n吃完了午饭之后，下午基本就是压北京的马路了。根据乐动力App记载，今天一共走了三万步二十多公里，我们是在用绳命在压北京的马路啊。\n\n本来决定去中关村，后来商量了决定走到五道口去看看。路上还去一个seveneleven买了瓶啤酒喝。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去五道口图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去五道口图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742401\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去五道口图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去五道口图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742444\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去五道口图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去五道口图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742455\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n途中经过一个Seven-Eleven店，进去买了一点儿东西，我们在里面的凹镜拍了一张照片，这竟然是我们今天唯一的一张合照，真是凄凉，一群乌鸦飞过的感觉。细看这张照片，蒋老板和常老板都挺妩媚的啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![出发去五道口图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"出发去五道口图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742463\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n接着我们就在北京的路上走啊走啊，边走边聊，感觉还挺有意思。路过一小段铁路，赶紧拍了一张铁路照片，老早就想拍这样的照片了，这应该是第一次站在还在使用的铁路中间拍，值得纪念一下。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149205-4\" data-poitype=\"4\"  poi-id=\"705189\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[北京外国语大学](https://travel.qunar.com/p-oi705189-beijingwaiguoyudaxue \"北京外国语大学\")</div>[1篇游记中提到](//travel.qunar.com/travelbook/list/4-beijingwaiguoyudaxue-705189.htm)<div data-eleId=\"705189\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>8：00-22：00\n*   <span>电话：</span>010-88816215,010-88816200\n*   <span>地址：</span>北京市海淀区西三环北路2号\n*   [查看详情](https://travel.qunar.com/p-oi705189-beijingwaiguoyudaxue)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n走着走着没了目的地，商量了一下想着去北京外国语大学看美女去，于是就奔着过去了。北外的校园很漂亮，我们在北外的一个操场边坐着聊了一个小时的人生，获益良多啊。这一个小时是人生中很难得的一段回忆。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京外国语大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京外国语大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742471\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:600px\">![北京外国语大学图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京外国语大学图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742475\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n我们就坐在这样的长椅上谈人生。他两的人生都已经有了目标，反观我自己的，前途一片迷茫啊，奔三的人了还不清楚自己以后要干啥，终于有点儿着急的感觉了，以后一定发愤图强，嗯。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149215-5\" data-poitype=\"5\"  poi-id=\"0\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">火炉火(五道口店)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>类型：</span>韩国料理\n*   <span>地址：</span>海淀区王庄路33号东王庄小区33号楼1楼(近清华东路)\n*   <span>电话：</span>010-62394475\n*   <span>简介：</span>韩式烧烤专门店。刚点完菜就被送的小菜“给震住了”，“摆了一桌子”，还可以“续碟”，“喜欢”土豆泥，南瓜羹也“超级好喝”。肉类质量确实不错，价格“在那儿摆着呢”，<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n聊着聊着就聊到了晚饭，常土著推荐了一家很火的韩国菜餐厅&mdash;&mdash;火炉火。于是五点多点儿我们三就赶到了这里，排队的人真多啊，所以味道一定很不错。后来尝试了一下，这里的味道确实蛮好的，值得推荐。至于这里在哪儿，看到这里的你就只有网上查查了，嘿嘿。这家店的地址是：北京市海淀区清华东路东王庄小区33号楼1楼。贴上火炉火的排队号卡一张。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![火炉火(五道口店)图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"火炉火(五道口店)图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742486\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![火炉火(五道口店)图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"火炉火(五道口店)图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742496\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n晚饭在火炉火吃得很开心，蒋老板请客，十分感谢。火炉火的东西很精致，量也不多，但是他的碳烤方式令人感觉耳目一新，反正我是第一次在桌子上用碳烤自己弄东西吃，感觉蛮不错的。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![火炉火(五道口店)图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"火炉火(五道口店)图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742517\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n吃完了晚饭，我们继续在北京溜达，溜达着就走到了中关村。到达中关村的时候已经为晚上八九点钟了。作为一枚挨踢男，中关村怎么说也是心目中一个圣地，所以到了这里心情还是蛮激动的，蒋老板所在的中科院也在这一带，牛逼死了。中关村跟我想象的不太一样，在这里看到了许多的IT公司总部，但是跟我想象中的不太一样。\n\n在中关村溜达了一会儿后我们就分开了，大家各自乘坐地铁回家，今天是很难忘的一天，十分感谢常老板和蒋老板的陪伴。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div></div><!-- /period_ct end --></div><!-- /e_day end --><!-- e_day start --><div id=\"day-15735994\" data-dayidx=\"day-15735994\" class=\"e_day day_style0 day-15735994\">\n\n#### <dl class=\"day_title\"><dt class=\"clrfix\"><div class=\"style\"></div><div class=\"text\">第4天</div></dt></dl>\n<!-- period_ct start --><div class=\"period_ct\"><div class=\"b_poi_info b_poi_item\" id=\"ele-1550289-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">美味早餐</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n今天晚上住在东城区细管胡同的一家青年旅社，一个人住了个四人间，天知道我原本定的是六人间。早上还是很任性的睡到九点钟才起床，出来旅行还很少这么悠闲，估计是太累了。逛了逛细管胡同后，在胡同外找了一家店吃早饭。结果这家店早饭超级赞，既实惠又好吃，一早起来人品爆发。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![美味早餐图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"美味早餐图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742541\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![美味早餐图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"美味早餐图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742547\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这一家店就在细管胡同外面不远处，很值得推荐哦。下面就是我的早饭了，油饼，豆花，还有一种不认识，里面有肥肠，感觉味道不错。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![美味早餐图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"美味早餐图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742548\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n吃完早饭之后又开始在北京随意溜达了，感觉这样很悠闲。大街上的人还不是很多，今天依旧是个艳阳天。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![美味早餐图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"美味早餐图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742550\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149236-4\" data-poitype=\"4\"  poi-id=\"7564212\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[长安街](https://travel.qunar.com/p-oi7564212-zhanganjie \"长安街\")</div>[13篇游记中提到](//travel.qunar.com/travelbook/list/4-zhanganjie-7564212.htm)<div data-eleId=\"7564212\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>开放时间：</span>全天\n*   <span>地址：</span>北京市东城区广场西侧路与西长安街交叉口东150米\n*   [查看详情](https://travel.qunar.com/p-oi7564212-zhanganjie)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n想了想，还是决定去看看故宫，感觉不去会遗憾，于是就公交车赶往天安门，照例在前一个站下了车。\n\n步行走过长安街，再次感受下长安街。长安街总是给我很舒服的感觉，就是高端大气上档次。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![长安街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"长安街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742551\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![长安街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"长安街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742553\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n没走多会儿就走到了前天来过的地方，进去天安门的安检口，今天人就少了许多，我刚开始还挺纳闷的。后来才知道为什么，还是自己蠢啊。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![长安街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"长安街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742555\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n前天已经看过了天安门，所以今天都没那么惊奇了，不过还是要赞一个的。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![长安街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"长安街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742560\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n因为今天打算去看故宫，所以就沿着天安门走了进去。进入城门洞的时候，遇见了一辆车开出来，当时我就挺惊讶的，抓拍了一张。一直没弄懂怎么会有车从这里面出来的，太令人惊讶了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![长安街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"长安街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742565\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n进入天安门之后，还没有到故宫的购票口，还要走过一个大广场，今天广场上人也不是很多，我还在暗自庆幸。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550294-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">国旗护卫队</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n【国旗护卫队】国旗护卫队的训练是在公共场合进行的，进入天安门的广场就能够看到他们了。今天天气很不错，来围观这些帅哥的人非常多，好不容易才挤了进去。一眼看见了一辆帅气的车子，如果能成为这种车的一个司机，我倒是不介意退休之后去干全职司机，这车车太霸气，瞬间感觉民用车弱爆了。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![国旗护卫队图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"国旗护卫队图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742739\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n贴几张国旗护卫队的训练照片，帅呆了。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![国旗护卫队图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"国旗护卫队图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742736\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![国旗护卫队图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"国旗护卫队图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742738\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![国旗护卫队图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"国旗护卫队图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742743\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149309-4\" data-poitype=\"4\"  poi-id=\"710603\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[故宫](https://travel.qunar.com/p-oi710603-gugong \"故宫\")</div>[151篇游记中提到](//travel.qunar.com/travelbook/list/4-gugong-710603.htm)<div data-eleId=\"710603\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>旺季:60元 淡季:40元。参观珍宝馆、钟表馆各需另外收费10元。\n*   <span>开放时间：</span>旺季：8:30-17:00  淡季：8:30-16:30，周一 不开放，法定节假日周一不闭馆\n*   <span>电话：</span>010-85007938,010-85007421,010-85007058\n*   <span>地址：</span>北京市东城区景山前街4号\n*   <span>简介：</span>世界五大宫之首，是中国明清两代的皇家宫殿。\n*   [查看详情](https://travel.qunar.com/p-oi710603-gugong)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n广场上人不是很多，只有稀稀拉拉的一些人在坐着休息，我也找了一个地方坐了一会儿，阳光下感觉很舒服。\n\n【故宫】故宫是明清两代的皇宫，经过无数次的改建，成为了现在的样子。以前的皇家生活的地方，相信有点儿好奇心的人都想来这里看看的。虽然做不了皇帝，去看看也是不错的。好不容易走到了大门口，心情已经开始有点儿激动了。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![故宫图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"故宫图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742752\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149316-4\" data-poitype=\"4\"  poi-id=\"705480\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[景山公园](https://travel.qunar.com/p-oi705480-jingshangongyuan \"景山公园\")</div>[68篇游记中提到](//travel.qunar.com/travelbook/list/4-jingshangongyuan-705480.htm)<div data-eleId=\"705480\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>2元\n*   <span>开放时间：</span>11月-次年3月：6:30-20:00(19:30止票)，4月-10月：6:30-21:00(20:30止票)。 景山公园寿皇殿建筑群开放时间：9:00—16:00，非节假日周一闭馆\n*   <span>电话：</span>010-64038098,010-64038090\n*   <span>地址：</span>北京市西城区景山前街景山公园\n*   <span>简介：</span>崇祯皇帝自缢处，现在则是俯瞰、拍摄故宫全景的最佳地点。\n*   [查看详情](https://travel.qunar.com/p-oi705480-jingshangongyuan)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n咦，奇怪，怎么一个人都没有呢？噢，尼玛，听旁边哥们儿说今天周一不开放。我去，我网上查不是说每天都开放嘛，我刚才还心情激动不能自己，一下子心情就跌入了低谷，看来这次北京行是去不了故宫了，怪自己太蠢。\n\n心情小小低落了一下，立马重整精神，选择下一个目的地。依稀想起前两天听一个朋友说过，在景山公园能够看到故宫的全景，于是立马决定去景山公园了。在中山公园门口有摆渡车，可以将人拉到后门去，感觉还蛮方便的。\n\n想办法换乘了5号线之后，很快就到了景山公园。景山公园也需要购票，2元一个人倒是真心不算贵，旅行了这么久，这绝对是最便宜的门票，没有之一。\n\n进了景山公园我就直奔公园里的山去了，想着可以看到紫禁城全景心情也是激动不已。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742779\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742778\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742782\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n登上一个山顶之后一回头，发现果然看见了故宫全景，这一天阳光很不错，可惜是逆光。紫禁城确实雄伟壮丽，不过只能美景在心中了，相机拍不出感觉。挑一张最好的吧。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742790\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n就凭借着视野，景山公园必须推荐了。看了好一会儿的紫禁城，继续在这周围闲逛着。一会儿发现了一对令人羡慕的情侣。（偷拍无罪O(&cap;_&cap;)O）\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742795\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在山顶上的房子里遇见了一个妈妈给女儿拍照。女儿小小年纪，很会摆pose。我看旁边给她拍照的游客不少，就顺便也来了两张。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![景山公园图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"景山公园图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742802\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n靠着栏杆坐下，在这里晒了一会儿太阳，懒洋洋的有种不想离开的感觉了。不过想着下午还要去798，就还是起来了，离开之前再次拍了拍紫禁城。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550324-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">崇祯帝自缢处</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n相信对历史有点儿了解的都知道，明朝的历史结束于明崇祯皇帝。而崇祯皇帝死得也窝囊&mdash;&mdash;自挂东南枝。是我国历史上第一个自缢而死的皇帝，唯一值得欣慰的就是临死还有一个忠心耿耿的太监陪着一起死。下图就是崇祯皇帝自缢处。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![崇祯帝自缢处图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"崇祯帝自缢处图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742831\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550325-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">北京沿街</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n顺着一条路就出了景山公园，来到了北京的大街上。北京的大街很有特色，路边全是一种树，这种树在这个季节落光了叶子（或者，我也不知道这种树有没有叶子），使人感觉有点儿萧条。不过路上行驶的汽车又给他增添了一丝生机。这种街道的感觉，是我对北京的一大印象。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京沿街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京沿街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742845\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京沿街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京沿街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742850\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京沿街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京沿街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742862\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n在寻找去798的公交车的路程中，很是经历了一番曲折，说多了都是泪，强烈建议开通798专线有木有。不过路上的风景还不错，阳光明媚令人心情愉悦。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149370-4\" data-poitype=\"4\"  poi-id=\"722952\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[798艺术区](https://travel.qunar.com/p-oi722952-798yishuqu \"798艺术区\")</div>[58篇游记中提到](//travel.qunar.com/travelbook/list/4-798yishuqu-722952.htm)<div data-eleId=\"722952\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>艺术区免费开放，个别艺术中心及画廊另外收费。\n*   <span>开放时间：</span>艺术区全天开放，画廊10:00-18:00（大部分周一闭馆）。\n*   <span>电话：</span>010-59789114,010-59789798,010-59789870\n*   <span>地址：</span>北京市朝阳区酒仙桥路2号\n*   <span>简介：</span>厂房改造的艺术中心，雕塑、画廊、特色店铺等文艺范十足。\n*   [查看详情](https://travel.qunar.com/p-oi722952-798yishuqu)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n经过一番曲折，好歹是到达了北京798艺术区，心情比较激动是真的。\n\n北京798艺术区是一个我很早就听过的地方，我虽然不学习艺术，也不关注艺术，但是有机会我还是很乐意去接触艺术的，我坚信生后不能没了艺术，所以对这个地方还是有点儿小期待的说。下车看见798三个红红的字，心跳就已经加速了，迫不及待的赶了过去。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742873\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742881\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742883\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n这次在798就仔细的看了一个艺术展，是一个叫做曹勇的画家的。一进入这个地方，立刻就被他的画吸引了，虽然不懂艺术，但是很喜欢他的画风，也佩服他的人生经历。所以在曹勇艺术展里面仔细的看完了大部分的画作，看了介绍他生平的片子，临走还买了一些纪念品，明信片。看过了这一个地方，就对798有了一个大致的印象和了解，所谓见微知著。看完曹勇艺术展出来已经过了好一会儿了，艺术确实让时间过得更快，让生活更美好。在798艺术区里面到处闲逛消磨了接下来的一小时。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742885\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742886\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742889\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742893\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![798艺术区图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"798艺术区图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742899\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n再仔细逛完798，时间已经到了下午三四点钟了。在798的一个商店里面购买了一些几年品，大部分是给我妹买的，想着她是学艺术的应该比较有兴趣。不过798里面的东西真心贵啊，不过好歹还是买了一些。为了赶上晚上九点过的飞机，我没有多少时间再逗留在北京了。想着来趟北京，总是要带点儿特产的吧，于是抓紧最后的时间赶往前门大街，打算去全聚德购买正宗的北京烤鸭。还好公交比较方便，顺利到了前门。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149381-4\" data-poitype=\"4\"  poi-id=\"713871\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[前门大街](https://travel.qunar.com/p-oi713871-qianmendajie \"前门大街\")</div>[29篇游记中提到](//travel.qunar.com/travelbook/list/4-qianmendajie-713871.htm)<div data-eleId=\"713871\" class=\"piao_info js_piaoinfo clrfix\"><div class=\"price\">门票<span class=\"pr\">_¥_</span></div>[预订](#)</div>\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>门票价格：</span>免费\n*   <span>开放时间：</span>全天开放，店铺营业时间各不相同，大约8:00-22:00左右。\n*   <span>电话：</span>010-67060009,(010)65253176\n*   <span>地址：</span>北京市东城区前门大街\n*   <span>简介：</span>京味十足的商业老街，各类小吃让人大快朵颐！\n*   [查看详情](https://travel.qunar.com/p-oi713871-qianmendajie)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n前门是一个很不错的地方，主要是购物。这次来的时间匆匆，购完物就得离开让我有点儿遗憾，因为我发现前门是一个可以慢慢逛的地方，很推荐。这里的建筑蛮不错，关键是这里的游人也是慢慢在走着，不见匆忙感，令人觉得舒服。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![前门大街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"前门大街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742905\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:453px\">![前门大街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"前门大街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742912\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n一走到前门，就看到了醒目的装饰品，下面写着前门暖冬四个字，令人心里的确是暖暖的。\n</div></dd></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![前门大街图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"前门大街图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742916\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n前门大街上竟然又两条小轨道，上面还有观光车在开，瞬间就把我吸引了。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-15149397-5\" data-poitype=\"5\"  poi-id=\"5235847\"  data-dist-id=\"299914\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">[北京全聚德(前门店)](https://travel.qunar.com/p-oi5235847-beijingquanjude \"北京全聚德(前门店)\")</div>[3篇游记中提到](//travel.qunar.com/travelbook/list/5-beijingquanjude-5235847.htm)\n<div class=\"info\"><div class=\"main_ct\">\n\n*   <span>类型：</span>烤鸭的满汉全席\n*   <span>地址：</span>前门大街30号(近前门步行街)\n*   <span>电话：</span>010-65112418,010-67011379\n*   <span>简介：</span>百余年历史打造精品烤鸭，口味誉满全球\n*   [查看详情](https://travel.qunar.com/p-oi5235847-beijingquanjude)<div class=\"triangle\"></div></div></div><div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n不过想着时间匆匆，也没时间细细感受了，赶紧去找全聚德。\n\n【全聚德】北京老字号了，在这里可以购买和品尝到正宗的北京烤鸭，而且在这里还可以品尝到北京美食。全聚德的人气蛮高，虽然是淡季了，虽然是下午这个时候，来卖烤鸭的人还是不少。\n</div><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京全聚德(前门店)图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京全聚德(前门店)图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742925\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl><dl class=\"js_memo_node\"><dt style=\"width:680px\">![北京全聚德(前门店)图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"北京全聚德(前门店)图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742929\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt><dd class=\"img_txt_wrap\"><span class=\"tri\"></span><div class=\"img_txt\">\n\n买了一只北京烤鸭，140元，包装倒是蛮精致的，想着要提着它回家也是有点儿伤感。这一次的北京行基本这样就结束了，临走时有去了稻香村的专卖店儿买了许多的特产，有三百多的样子，事后证明稻香村的许多东西确实味道很好，值得购买。\n\n匆匆的离开了前门大街，最后来了一张照片。\n</div></dd></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div><div class=\"b_poi_info b_poi_item\" id=\"ele-1550343-2\" data-poitype=\"2\"  poi-id=\"0\"  data-dist-id=\"0\" element-type=event><div class=\"top\">\n\n##### <span class=\"iconfont\"></span><div class=\"b_poi_title_box\">再见北京</div>\n<div style=\"display:none\" class=\"b_poitext_ad\"></div></div><div class=\"bottom\"><div class=\"e_img_schedule\"><div class=\"imglst\"><div class=\"text js_memo_node\">\n\n这一次的北京行，是由许多的运气和巧合促成，虽然来也匆匆去也匆匆，可是也算是尽情领略到了北京-帝都的美丽，也圆了自己的首都梦，真心很赞。\n\n北京是一个很不错的城市，尽管来之前，身边有人诋毁它，有人赞赏它，但只有通过自己亲身经历，我才明白，我喜欢这座城市。它有历史，有积淀，有活力，有机会，还有浓浓的北京风味儿，这一切都令我着迷。虽然我不喜欢这里快节奏的生活，但是我不介意来这里进行短暂的停留，总之一句话，北京是一个很有魅力的城市，它跟我之前去过的很多地方都不太一样，它处处都让人感觉到一种独特的美，令人难以忘怀。\n\n许多人爱旅行，其实是爱这样一种状态：啊，开始旅行啦！好难得出门啊！我要过另外一种生活！我要把工作都抛掉！我要把平时攒的钱都用上！我要把日常对自己的压榨和抠门，都在旅行中找补回来。\n\n在旅行中，人们就像是面对情妇一般，因为过上了远离日常生活的另一种未知而新鲜的生活，于是格外的慷慨，特别好奇，尤其大惊小怪，经常一惊一乍，你会更认真地区端详他乡的天空，而你在故乡可能从来没有注意过天空的颜色；他乡的狗都比较可爱，尽管你在你的故乡可能特别讨厌狗；他乡的空气都那么沁人心脾，而家乡的空气，你从来没有仔细闻过；他乡的劣质手工小商品，颜色似乎都比家乡的鲜艳些，能让你情不自禁的掏出钱包来。\n\n这也让我开始反思，热爱旅行的人们如我，还没有仔细的去感受过自己的家乡。而我的家乡重庆，对许多人来说又何尝不是一个旅行的目的地呢。人们总是忽略身边拥有的人和事，而把关注的眼光投到远方。我不想再这样了。突然就厌倦了旅行，决定再接下来的一段时间，我要用心的去感受家乡重庆，以旅行的心态去感受她，以旅行的眼光去看待它，以旅行的方式去发现她，我也希望借此能够学会珍惜拥有。\n\n与其说旅行比生活美满得多，不让说我们试图让旅行比生活美满。旅行和梦不一样，做梦你不知道何时结束，而旅行你知道何时结束。许多人都存在着这样一种心思：放在身边的，随时都能够观赏，所以还是先瞻望远处好了，身边的东西下次再说，下次再说。。。很多时候，就这么下次再说，终于是错过了。如果不能明白这里道理，或许有一天当我真的踏遍了全中国，走遍了全世界，回过头来却发现自己最不了解的地方是自己的家乡，我简直无法想象那场景会是多么的讽刺。\n\n不总指望着把好好享受的希望寄托在别处、未来和旅途上，而是尝试把自己的生活安排得好些，不总指望着命运给你埋伏着许多段一见钟情，而对身边的伴侣好一些，那样，生活会更加的真实而幸福。平时对自己好一些，旅行时才能少些幻想，真正的去体验旅行的快乐。\n\n有些话借用湖北教育出版社出版的《我这个普通人的生后》一书，仅此表明。\n\n这次旅行最大的一个改变，就是在旅行的最后一刻突然厌倦了一个人，突然特别想要有个人陪。所以，希望那个对的人尽快出现吧。这次旅行让我学会珍惜拥有，十分感恩，借用晏殊的诗句作为结尾，与你我共勉：\n\n满目山河空念远\n\n落花风雨更伤春\n\n不如怜取眼前人\n\n不管别人说北京好与坏，不管你心里面认为北京好与坏，真正喜不喜欢，还是只有亲自来了你才会知道，所以，如果心动了，就出发吧。\n</div><dl class=\"js_memo_node\"><dt style=\"width:385px\">![再见北京图片](https://source.qunarzz.com/site/images/travel/finish/space_3nd.png \"再见北京图片\")<div class=\"img_btn_wrap\"><span class=\"img_btn img_btn_digg iconfont\" data-id=\"7742940\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span></div></dt></dl></div></div><div class=\"ops\"><div class=\"inner clrfix\"><div class=\"left\"></div><div class=\"right\">[评论](javascript:;)</div></div></div></div></div></div><!-- /period_ct end --></div><!-- /e_day end --></div></div><!-- 行程安排end --><!-- 底部UGC start --><div class=\"b_link_part\">\n\n*   <div class=\"likeCT\">[<span class=\"iconfont\"></span>赞<span class=\"num\">939</span>](javascript:;)\n\n    +1\n</div>\n*   <div class=\"collectCT\">[<span class=\"iconfont\"></span>收藏<span>1742</span>](javascript:;)</div>\n*   <div class=\"shartCT\"><span class=\"sub_item\" href=\"javascript:;\">分享<span>143</span></span>\n\n        *   [微博](javascript:;)\n    *   [微信](javascript:;)\n    *   [qq空间](javascript:;)</div></div><!-- 底部UGC end --><!-- 最新评论 start --><div class=\"b_comment_main\" name=\"b_recent_comment\" id=\"b_recent_comment\"><div class=\"hd\"><!-- <div class=\"txt_info\">嗨，快来赞美一下作者吧，前10位发表评论的用户还有积分领取哦！[了解详情](//travel.qunar.com/travelbook/topic/2888)</div> -->\n\n### 最新评论(47条)\n</div><div class=\"e_ct\"><dl class=\"e_comment_add clrfix js_commentbox\"><dt class=\"e_ct_l\">[![zeom2600](https://qcommons.qunar.com/headshot/headshotsById/1036105870.png?s&amp;ssl=true \"zeom2600\")](//travel.qunar.com/space/1036105870@qunar)[zeom2600](//travel.qunar.com/space/1036105870@qunar \"zeom2600\")</dt><dd class=\"e_ct_r\"><div class=\"textarea_box\"><t